const delay=(interval=1000)=>{
  return new Promise(resolve=>{
    setTimeout(()=>{
      resolve(`@@${interval}`)
    },interval)
  })
}


// 需求:串行请求，有三个请求[请求需要的时间分别是1000/2000/3000]?
delay(1000).then(value=>{
  console.log('第一个请求成功',value)

  return delay(2000)
}).then(value=>{
  console.log('第二个请求成功',value)
})
