import {take,takeEvery,takeLatest,throttle,debounce,call,apply,fork,delay,put} from 'redux-saga/effects'
import * as TYPES from './action-types'
// 创建监听器，监听派发的仍无
const saga=function* saga(){
  yield take(`${TYPES.DEMO_COUNT}`)


}


export default saga
