import {take,takeEvery,takeLatest,throttle,debounce,call,apply,fork,delay,put,select} from 'redux-saga/effects'
import * as TYPES from './action-types'


const api={
  queryData(){
    return new Promise(resolve=>{
      setTimeout(()=>{
        let result={
          code:0,
          data:[10,20,30,40]
        }
        resolve(res)

      },2000)
    })
  }
}
// 创建执行函数，再任务被监听后，去做异步操作[Generator函数]
const workingCount=function* workingCount(action){
  console.log('被监听到了',action)
  yield delay(2000)//启动一个延迟函数 模拟向服务器发送请求

  // 派发任务，通知reducer执行
  yield put({
    type:TYPES.DEMO_COUNT,
    payload:action.payload
  })





}

// 创建监听器，监听派发的仍无
const saga=function* saga(){
  yield takeLatest(`${TYPES.DEMO_COUNT}@SAGA@`,workingCount)

}


export default saga
